import React, { useState } from 'react';
import { Link, useMatch, useResolvedPath } from 'react-router-dom';

export default function Navbar ()
{
  const [ isOpen, setIsOpen ] = useState( false );

  // function handleClick ()
  // {
  //   setIsOpen( !isOpen );
  // }

  return (
    <nav className="nav">
      <Link to="/" className="site-title">
        HealthyGram
      </Link>
      <ul>
        <CustomLink to="/">Home</CustomLink>
        <CustomLink to="/dashboard">Dashboard</CustomLink>
        <CustomLink to="/recordList">Records list</CustomLink>
        <CustomLink to="/about">Doctor</CustomLink>
        <CustomLink to="/about">Patient</CustomLink>
        <CustomLink to="/about" hasDropdown>
          Register
          <ul className={ isOpen ? 'dropdown open' : 'dropdown' }>
            <li>
              <Link to="/register-doctor">Register as Doctor</Link>
            </li>
            <li>
              <Link to="/register-patient">Register as Patient</Link>
            </li>
          </ul>
        </CustomLink>
      </ul>
    </nav>
  );
}

function CustomLink ( { to, children, hasDropdown, ...props } )
{
  const resolvedPath = useResolvedPath( to );
  const isActive = useMatch( { path: resolvedPath.pathname, end: true } );

  function handleClick ()
  {
    setIsOpen( !isOpen );
  }

  return (
    <li className={ isActive ? 'active' : '' }>
      { hasDropdown ? (
        <a href="/" onClick={ handleClick }>
          { children }
        </a>
      ) : (
        <Link to={ to } { ...props }>
          { children }
        </Link>
      ) }
    </li>
  );
}
